class MDHTMLEditor{
    #props = {
        document: null,
        container: null,
    };
    #controls = {
        textArea: null,
        btnCopy: null,
        btnCut: null,
        btnPaste: null,
        btnUndo: null,
        btnRedo: null,
        btnBold: null,
        btnItalic: null,
        btnUnderLine: null,
        btnAlignLeft: null,
        btnAlignCenter: null,
        btnAlignRight: null,
        btnIndentLeft: null,
        btnIndentRight: null,
        btnList: null,
        btnInsertImage: null,
        drpFontName: null,
        drpFontSize: null,
        pnlInsertImage: null,
        edbInsertImageInput: null,
        btnInsertImageSubmit: null,
        btnInsertImageCancel: null,
        edbStatus: null,
    };
    #constant = {
        uid: null,
        copyIcon: 'data:image/svg+xml;base64,',
        cutIcon: 'data:image/svg+xml;base64,',
        pasteIcon: 'data:image/svg+xml;base64,',
        undoIcon: 'data:image/svg+xml;base64,',
        redoIcon: 'data:image/svg+xml;base64,',
        boldIcon: 'data:image/svg+xml;base64,',
        underlineIcon: 'data:image/svg+xml;base64,',
        italicIcon: 'data:image/svg+xml;base64,',
        centerAlignIcon: 'data:image/svg+xml;base64,',
        leftAlignIcon: 'data:image/svg+xml;base64,',
        rightAlignIcon: 'data:image/svg+xml;base64,',
        rightIndentIcon: 'data:image/svg+xml;base64,',
        leftIndentIcon: 'data:image/svg+xml;base64,',
        listingIcon: 'data:image/svg+xml;base64,',
        imageIcon: 'data:image/svg+xml;base64,',
        fontIcon: 'data:image/svg+xml;base64,',
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    /*-----------------------------------------------|Initiate Methods|-----------------------------------------------*/
    /*----------------------------------------------------------------------------------------------------------------*/
    constructor(document, container) {
        this.#props.container = container;
        this.#props.document = document;
        this.#constant.uid = this.generateGuid();
    }
    /*----------------------------------------------------------------------------------------------------------------*/
    initiateControl() {
        this.#props.container.innerHTML = this.render();
        this.initiateElements();
    }
    /*----------------------------------------------------------------------------------------------------------------*/
    initiateElements() {
        try {
            this.#controls.textArea = this.#props.document.getElementById('md-html-editor-text-' + this.#constant.uid);

            this.#controls.btnCopy = this.#props.document.getElementById('md-html-editor-copy-button-' + this.#constant.uid);
            this.#controls.btnCut = this.#props.document.getElementById('md-html-editor-cut-button-' + this.#constant.uid);
            this.#controls.btnPaste = this.#props.document.getElementById('md-html-editor-past-button-' + this.#constant.uid);
            this.#controls.btnUndo = this.#props.document.getElementById('md-html-editor-undo-button-' + this.#constant.uid);
            this.#controls.btnRedo = this.#props.document.getElementById('md-html-editor-redo-button-' + this.#constant.uid);
            this.#controls.btnBold = this.#props.document.getElementById('md-html-editor-bold-button-' + this.#constant.uid);
            this.#controls.btnUnderLine = this.#props.document.getElementById('md-html-editor-underline-button-' + this.#constant.uid);
            this.#controls.btnItalic = this.#props.document.getElementById('md-html-editor-italic-button-' + this.#constant.uid);
            this.#controls.btnAlignLeft = this.#props.document.getElementById('md-html-editor-align-left-' + this.#constant.uid);
            this.#controls.btnAlignCenter = this.#props.document.getElementById('md-html-editor-align-center-' + this.#constant.uid);
            this.#controls.btnAlignRight = this.#props.document.getElementById('md-html-editor-align-right-' + this.#constant.uid);
            this.#controls.btnIndentLeft = this.#props.document.getElementById('md-html-editor-indent-left-' + this.#constant.uid);
            this.#controls.btnIndentRight = this.#props.document.getElementById('md-html-editor-indent-right-' + this.#constant.uid);
            this.#controls.btnList = this.#props.document.getElementById('md-html-editor-list-button-' + this.#constant.uid);
            this.#controls.btnFont = this.#props.document.getElementById('md-html-editor-font-button-' + this.#constant.uid);
            this.#controls.btnFontSize = this.#props.document.getElementById('md-html-editor-font-size-button-' + this.#constant.uid);
            this.#controls.btnInsertImage = this.#props.document.getElementById('md-html-editor-image-button-' + this.#constant.uid);
            this.#controls.drpFontName = this.#props.document.getElementById('md-html-editor-font-drop-down-' + this.#constant.uid);
            this.#controls.drpFontSize = this.#props.document.getElementById('md-html-editor-font-size-drop-down-' + this.#constant.uid);
            this.#controls.pnlInsertImage = this.#props.document.getElementById('md-html-editor-menu-line-insert-image-' + this.#constant.uid);
            this.#controls.pnlInsertImage.style.display = 'none';
            this.#controls.btnInsertImageSubmit = this.#props.document.getElementById('md-html-editor-menu-line-insert-image-button-submit-' + this.#constant.uid);
            this.#controls.btnInsertImageCancel = this.#props.document.getElementById('md-html-editor-menu-line-insert-image-button-cancel-' + this.#constant.uid);
            this.#controls.edbInsertImageInput = this.#props.document.getElementById('md-html-editor-menu-line-insert-image-input-' + this.#constant.uid);
            this.#controls.edbStatus = this.#props.document.getElementById('md-html-editor-status-bar-content-' + this.#constant.uid);

            this.#controls.btnCopy.style.backgroundImage = 'url("' + this.#constant.copyIcon + '")';
            this.#controls.btnCut.style.backgroundImage = 'url("' + this.#constant.cutIcon + '")';
            this.#controls.btnPaste.style.backgroundImage = 'url("' + this.#constant.pasteIcon + '")';
            this.#controls.btnBold.style.backgroundImage = 'url("' + this.#constant.boldIcon + '")';
            this.#controls.btnUnderLine.style.backgroundImage = 'url("' + this.#constant.underlineIcon + '")';
            this.#controls.btnUndo.style.backgroundImage = 'url("' + this.#constant.undoIcon + '")';
            this.#controls.btnRedo.style.backgroundImage = 'url("' + this.#constant.redoIcon + '")';
            this.#controls.btnItalic.style.backgroundImage = 'url("' + this.#constant.italicIcon + '")';
            this.#controls.btnAlignLeft.style.backgroundImage = 'url("' + this.#constant.leftAlignIcon + '")';
            this.#controls.btnAlignCenter.style.backgroundImage = 'url("' + this.#constant.centerAlignIcon + '")';
            this.#controls.btnAlignRight.style.backgroundImage = 'url("' + this.#constant.rightAlignIcon + '")';
            this.#controls.btnIndentLeft.style.backgroundImage = 'url("' + this.#constant.leftIndentIcon + '")';
            this.#controls.btnIndentRight.style.backgroundImage = 'url("' + this.#constant.rightIndentIcon + '")';
            this.#controls.btnList.style.backgroundImage = 'url("' + this.#constant.listingIcon + '")';
            this.#controls.btnInsertImage.style.backgroundImage = 'url("' + this.#constant.imageIcon + '")';

            this.InitiateEvent();
            return true;
        } catch (ex) {
            return false;
        }
    }
    /*----------------------------------------------------------------------------------------------------------------*/
    InitiateEvent() {
        try {
            this.#controls.textArea.onkeyup = this.textArea_onChange.bind(this);
            this.#controls.textArea.onclick = this.textArea_onChange.bind(this);

            this.#controls.btnCopy.onclick = this.btnCopy_onClick.bind(this);
            this.#controls.btnCut.onclick = this.btnCut_onClick.bind(this);
            this.#controls.btnPaste.onclick = this.btnPaste_onClick.bind(this);
            this.#controls.btnUndo.onclick = this.btnUndo_onClick.bind(this);
            this.#controls.btnRedo.onclick = this.btnRedo_onClick.bind(this);

            this.#controls.btnBold.onclick = this.btnBold_onClick.bind(this);
            this.#controls.btnUnderLine.onclick = this.btnUnderLine_onClick.bind(this);
            this.#controls.btnItalic.onclick = this.btnItalic_onClick.bind(this);
            this.#controls.btnAlignLeft.onclick = this.btnAlignLeft_onClick.bind(this);
            this.#controls.btnAlignCenter.onclick = this.btnAlignCenter_onClick.bind(this);
            this.#controls.btnAlignRight.onclick = this.btnAlignRight_onClick.bind(this);
            this.#controls.btnIndentRight.onclick = this.btnIndentRight_onClick.bind(this);
            this.#controls.btnIndentLeft.onclick = this.btnIndentLeft_onClick.bind(this);
            this.#controls.btnList.onclick = this.btnList_onClick.bind(this);
            this.#controls.btnInsertImage.onclick = this.btnInsertImage_onClick.bind(this);
            this.#controls.btnInsertImageSubmit.onclick = this.btnInsertImageSubmit_onclick.bind(this);
            this.#controls.btnInsertImageCancel.onclick = this.btnInsertImageCancel_onclick.bind(this);
            this.#controls.drpFontName.onchange = this.drpFontName_onchange.bind(this);
            this.#controls.drpFontSize.onchange = this.drpFontSize_onchange.bind(this);

            return true;
        } catch (ex) {
            return false;
        }
    }
    /*----------------------------------------------------------------------------------------------------------------*/
    /*---------------------------------------------------|Methods|----------------------------------------------------*/
    /*----------------------------------------------------------------------------------------------------------------*/
    generateGuid() {
        function s4() {
            return Math.floor((1 + Math.random()) * 0x10000)
                .toString(16)
                .substring(1);
        }

        return s4() + s4() + '-' + s4() + '-' + s4() + '-' +
            s4() + '-' + s4() + s4() + s4();
    }
    /*----------------------------------------------------------------------------------------------------------------*/
    getHTML = () => {
      return this.#controls.textArea.innerHTML;
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    setHTML = (html) => {
        this.#controls.textArea.innerHTML = html;
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    /*------------------------------------------------|Event Methods|-------------------------------------------------*/
    /*----------------------------------------------------------------------------------------------------------------*/
    textArea_onChange = () => {
        const num_ranges = this.#props.document.getSelection().rangeCount;

        // Will hold parent tags of a range
        let range_parent_tags;

        // Will hold parent tags of all ranges
        let all_ranges_parent_tags = [];

        // Current menu tags
        let menu_tags = [ 'B', 'UL', 'U', 'I' ];

        // Will hold common tags from all ranges
        let menu_tags_common = [];

        // For all ranges
        for(let i=0; i<num_ranges; i++) {
            // Start container of range
            const start_element = this.#props.document.getSelection().getRangeAt(i).startContainer;

            // End container of range
            const end_element = this.#props.document.getSelection().getRangeAt(i).endContainer;

            // Will hold parent tags of a range
            range_parent_tags = [];

            // If starting node and final node are the same
            if(start_element.isEqualNode(end_element)) {
                // If the current element lies inside the editor container then don't consider the range
                // This happens when editor container is clicked
                if(this.#controls.textArea.isEqualNode(start_element)) {
                    all_ranges_parent_tags.push([]);
                    continue;
                }

                let cur_element = start_element.parentNode;

                // Get all parent tags till editor container
                while(!this.#controls.textArea.isEqualNode(cur_element)) {
                    range_parent_tags.push(cur_element.nodeName);
                    cur_element = cur_element.parentNode;
                }
            }

            // Push tags of current range
            all_ranges_parent_tags.push(range_parent_tags);
        }

        // Find common parent tags for all ranges
        for(let i=0; i<menu_tags.length; i++) {
            let common_tag = 1;
            for (let j = 0; j < all_ranges_parent_tags.length; j++) {
                if (all_ranges_parent_tags[j].indexOf(menu_tags[i]) === -1) {
                    common_tag = -1;
                    break;
                }
            }

            if (common_tag === 1)
                menu_tags_common.push(menu_tags[i]);
        }

        // Highlight menu for common tags
        if(menu_tags_common.indexOf('B') !== -1)
            this.#controls.btnBold.classList.add("md-html-editor-menu-button-selected");
        else
            this.#controls.btnBold.classList.remove("md-html-editor-menu-button-selected");

        if(menu_tags_common.indexOf('U') !== -1)
            this.#controls.btnUnderLine.classList.add("md-html-editor-menu-button-selected");
        else
            this.#controls.btnUnderLine.classList.remove("md-html-editor-menu-button-selected");

        if(menu_tags_common.indexOf('I') !== -1)
            this.#controls.btnItalic.classList.add("md-html-editor-menu-button-selected");
        else
            this.#controls.btnItalic.classList.remove("md-html-editor-menu-button-selected");

        if(menu_tags_common.indexOf('UL') !== -1)
            this.#controls.btnList.classList.add("md-html-editor-menu-button-selected");
        else
            this.#controls.btnList.classList.remove("md-html-editor-menu-button-selected");
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnCopy_onClick = () => {
        document.execCommand('copy');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnCut_onClick = () => {
        document.execCommand('cut');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnPaste_onClick = () => {
        document.execCommand('paste');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnUndo_onClick = () => {
        document.execCommand('undo');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnRedo_onClick = () => {
        document.execCommand('redo');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnBold_onClick = (e) => {
        document.execCommand('bold');
        this.#controls.textArea.focus();
        if (e.target.classList.contains('md-html-editor-menu-button-selected')){
            e.target.classList.remove('md-html-editor-menu-button-selected');
        }
        else {
            e.target.classList.add('md-html-editor-menu-button-selected');
        }
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnItalic_onClick = (e) => {
        document.execCommand('italic');
        this.#controls.textArea.focus();
        if (e.target.classList.contains('md-html-editor-menu-button-selected')){
            e.target.classList.remove('md-html-editor-menu-button-selected');
        }
        else {
            e.target.classList.add('md-html-editor-menu-button-selected');
        }
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnUnderLine_onClick = (e) => {
        document.execCommand('underline');
        this.#controls.textArea.focus();
        if (e.target.classList.contains('md-html-editor-menu-button-selected')){
            e.target.classList.remove('md-html-editor-menu-button-selected');
        }
        else {
            e.target.classList.add('md-html-editor-menu-button-selected');
        }
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnAlignLeft_onClick = () => {
        document.execCommand('justifyLeft');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnAlignCenter_onClick = () => {
        document.execCommand('justifyCenter');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnAlignRight_onClick = () => {
        document.execCommand('justifyRight');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnIndentRight_onClick = () => {
        document.execCommand('indent');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnIndentLeft_onClick = () => {
        document.execCommand('outdent');
        this.#controls.textArea.focus();
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    drpFontName_onchange = () => {
        try {
            const fontName = this.#controls.drpFontName.options[this.#controls.drpFontName.selectedIndex].value;

            this.#controls.textArea.focus();
            document.execCommand("fontName", false, fontName);
            this.#controls.textArea.focus();
        }
        catch (ex) {
        }
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    drpFontSize_onchange = () => {
        try {
            const fontSize = this.#controls.drpFontSize.options[this.#controls.drpFontSize.selectedIndex].value;

            this.#controls.textArea.focus();
            document.execCommand("fontSize", false, fontSize);
            this.#controls.textArea.focus();
        }
        catch(ex){
        }
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnList_onClick = () => {
        document.execCommand('insertUnorderedList');
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnInsertImage_onClick = () => {
        if (this.#controls.pnlInsertImage.style.display === 'none'){
            this.#controls.pnlInsertImage.style.display = 'flex';
        } else {
            this.#controls.pnlInsertImage.style.display = 'none';
        }
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnInsertImageSubmit_onclick = () => {
        if (this.#controls.edbInsertImageInput.value !== "") {
            this.#controls.textArea.focus();
            document.execCommand("insertImage", false, this.#controls.edbInsertImageInput.value);
            this.#controls.textArea.focus();
            this.#controls.pnlInsertImage.style.display = 'none';
            this.#controls.edbInsertImageInput.value = '';
        }
        else {
            this.#controls.edbStatus.innerHTML = "The image url is empty";
        }
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    btnInsertImageCancel_onclick = () => {
        this.#controls.pnlInsertImage.style.display = "none";
    };
    /*------------------------------------------------|Render Methods|------------------------------------------------*/
    /*----------------------------------------------------------------------------------------------------------------*/
    renderInsertImagePanel = () => {
        return(
            '<div id="md-html-editor-menu-line-insert-image-' + this.#constant.uid + '" class="md-html-editor-menu-line-insert-image">'+
            '   <input id="md-html-editor-menu-line-insert-image-input-' + this.#constant.uid + '" placeholder="Enter image url here" type="url"/>' +
            '   <button id="md-html-editor-menu-line-insert-image-button-submit-' + this.#constant.uid + '" class="md-button">Insert</button>'+
            '   <button id="md-html-editor-menu-line-insert-image-button-cancel-' + this.#constant.uid + '" class="md-button">Cancel</button>'+
            '</div>'
        );
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    renderMenuLine2 = () => {
        return(
            '<div class="md-html-editor-menu-line2">'+
            '   <div class="md-html-editor-menu-line2-font-panel">' +
            '       <select id="md-html-editor-font-drop-down-' + this.#constant.uid + '" class="md-html-editor-menu-font-drop-down">' +
            '           <option value="sans-serif" class="md-html-editor-font-item-sans-serif">Sans Serif</option>' +
            '           <option value="times new roman" class="md-html-editor-font-item-serif">Times new roman</option>' +
            '           <option value="courier new" class="md-html-editor-font-item-fixed-width">courier new</option>' +
            '           <option value="arial black" class="md-html-editor-font-item-wide">Arial Black</option>' +
            '           <option value="arial narrow" class="md-html-editor-font-item-narrow">Arial Narrow</option>' +
            '           <option value="comic sans ms" class="md-html-editor-font-item-comic-sans-ms">Comic Sans MS</option>' +
            '           <option value="georgia" class="md-html-editor-font-item-georgia">Georgia</option>' +
            '           <option value="tahoma" class="md-html-editor-font-item-tahoma">Tahoma</option>' +
            '           <option value="trebuchet ms" class="md-html-editor-font-item-trebuchet-ms">trebuchet MS</option>' +
            '           <option value="verdana" class="md-html-editor-font-item-verdana">Verdana</option>' +
            '       </select>' +
            '   </div>'+
            '   <div class="md-html-editor-menu-line2-font-size-panel">'+
            '       <select id="md-html-editor-font-size-drop-down-' + this.#constant.uid + '" class="md-html-editor-menu-font-size-drop-down">' +
            '           <option value="1" class="md-html-editor-font-size-item-very-small">Very small</option>' +
            '           <option value="2" class="md-html-editor-font-size-item-small">Small</option>' +
            '           <option value="3" class="md-html-editor-font-size-item-less-normal">Less than normal</option>' +
            '           <option value="4" class="md-html-editor-font-size-item-normal" selected>Normal</option>' +
            '           <option value="5" class="md-html-editor-font-size-item-large">Large</option>' +
            '           <option value="6" class="md-html-editor-font-size-item-very-large">Very Large</option>' +
            '           <option value="7" class="md-html-editor-font-size-item-huge">Huge</option>' +
            '       </select>' +
            '   </div>'+
            '</div>'
        );
    }
    /*----------------------------------------------------------------------------------------------------------------*/
    renderMenu = () => (
        '<div class="md-html-editor-menu">'+
        '   <button id="md-html-editor-copy-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Copy"></button>' +
        '   <button id="md-html-editor-cut-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Cut"></button>' +
        '   <button id="md-html-editor-past-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Past"></button>' +

        '   <div class="md-html-editor-menu-separator"></div>'+
        '   <button id="md-html-editor-undo-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Undo"></button>' +
        '   <button id="md-html-editor-redo-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Redo"></button>' +

        '   <div class="md-html-editor-menu-separator"></div>'+
        '   <button id="md-html-editor-bold-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Bold"></button>' +
        '   <button id="md-html-editor-underline-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Underline"></button>' +
        '   <button id="md-html-editor-italic-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Italic"></button>' +

        '   <div class="md-html-editor-menu-separator"></div>'+
        '   <button id="md-html-editor-align-left-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Justify Left"></button>' +
        '   <button id="md-html-editor-align-center-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Justify Center"></button>' +
        '   <button id="md-html-editor-align-right-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Justify Right"></button>' +

        '   <div class="md-html-editor-menu-separator"></div>'+
        '   <button id="md-html-editor-indent-left-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="indent Left"></button>' +
        '   <button id="md-html-editor-indent-right-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="indent Right"></button>' +

        '   <div class="md-html-editor-menu-separator"></div>'+
        '   <button id="md-html-editor-list-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button" title="Bullet List"></button>' +
        '   <button id="md-html-editor-image-button-' + this.#constant.uid + '" class="md-button md-html-editor-menu-button md-html-editor-drop-down-button" title="Picture"></button>'+
        '   <div class="md-html-editor-menu-separator"></div>'+
        '</div>' +
        this.renderMenuLine2() +
        this.renderInsertImagePanel()
    );
    /*----------------------------------------------------------------------------------------------------------------*/
    renderStatusBar = () =>{
        return(
            '<div class="md-html-editor-status-bar">' +
            '   <p id="md-html-editor-status-bar-content-' + this.#constant.uid + '"></p>' +
            '</div>'
        );
    };
    /*----------------------------------------------------------------------------------------------------------------*/
    render() {
        return (
            '<div class="md-html-editor-container">' +
            this.renderMenu() +
            '<div id="md-html-editor-text-' + this.#constant.uid + '" role="textbox" class="md-html-editor-area" contenteditable="true" spellcheck="false"></div>'+
            this.renderStatusBar() +
            '</div>'
        );
    }
    /*----------------------------------------------------------------------------------------------------------------*/
}